mapper.update:
UPDATE TEAM_USERS
	SET USER_PW= #{userPw}, USER_NAME= #{userName}, USER_LOCATION = #{userLocation},USER_AGE = #{userAge},ROLE=#{role}
		WHERE USER_ID = #{userId}

UPDATE TEAM_USERS
SET ROLE= 'ROLE_ADMIN' ,TEAM_NAME = #{teamName} , TEAM_JOIN = 'ACCESS'
	WHERE USER_ID = #{userId}

mapper.select:
SELECT * 
	FROM TEAM_USERS 
		WHERE ROLE = 'ROLE_ADMIN' AND TEAM_NAME = #{teamName}

mapper.update:
UPDATE TEAM_USERS
	SET TEAM_NAME = #{teamName} , TEAM_JOIN = 'STANDBY'
		WHERE USER_ID = #{userId}

UPDATE TEAM_USERS
	SET ROLE = 'ROLE_MEMBER', TEAM_NAME = #{teamName}, TEAM_JOIN = 'ACCESS'
		WHERE USER_ID = #{userId}

UPDATE TEAM_USERS
	SET TEAM_NAME = #{teamName} , TEAM_JOIN = 'DENIED'
		WHERE USER_ID = #{userId}
